import {
  ADD_TODO,
  DELETE_TODO,
  EDIT_TODO,
  FETCH_ALL_TODOS,
  SORT_UP,
  SORT_DOWN,
  FILTER_PRIORITY,
} from './types';

const initialState = {
  todos: [],
};

export default function todoAppReducer(state = initialState, action) {
  switch (action.type) {
    case FETCH_ALL_TODOS:
      return {
        ...state,
        todos: [...action.payload],
        history: [...action.payload],
      };

    case ADD_TODO:
      return { ...state, todos: [...state.todos, action.payload] };

    case DELETE_TODO:
      return {
        ...state,
        todos: [...state.todos.filter((todo) => todo.uuid != action.payload)],
      };

    case EDIT_TODO:
      return {
        ...state,
        todos: [
          ...state.todos.map((todo) =>
            todo.uuid == action.payload.uuid ? action.payload : todo
          ),
        ],
      };

    case SORT_UP:
      return {
        ...state,
        todos: [...state.todos.sort((a, b) => a.id - b.id)],
      };

    case SORT_DOWN:
      return {
        ...state,
        todos: [...state.todos.sort((a, b) => b.id - a.id)],
      };

    case FILTER_PRIORITY:
      return {
        ...state,
        todos: [...history.todos.filter((item) => item.priority - a.id)],
      };

    default:
      return state;
  }
}
