import {
  ADD_TODO,
  DELETE_TODO,
  EDIT_TODO,
  COMPLETE_TODO,
  IS_EDIT_TODO,
  FETCH_ALL_TODOS,
  SORT_UP,
  SORT_DOWN,
} from './types';

const initialState = {
  todos: [],
};

export default function todoAppReducer(state = initialState, action) {
  switch (action.type) {
    case FETCH_ALL_TODOS:
      return { ...state, todos: [...action.payload] };

    case ADD_TODO:
      return { ...state, todos: [...state.todos, {...action.payload, id}] };

    case DELETE_TODO:
      return {
        ...state,
        todos: [...state.todos.filter((todo) => todo.uuid != action.payload)],
      };

    case EDIT_TODO:
      return {
        ...state,
        todos: [
          ...state.todos.map((todo) =>
            todo.uuid == action.payload.uuid ? action.payload : todo
          ),
        ],
      };

    case SORT_UP:
      return {
        ...state,
        todos: [...state.todos.sort((a, b) => a.id - b.id)],
      };

    case SORT_DOWN:
      return {
        ...state,
        todos: [...state.todos.sort((a, b) => b.id - a.id)],
      };

    // case DELETE_TODO:
    //   return state.filter((item) => item.id !== action.payload.id);
    // case IS_EDIT_TODO:
    //   return state.map((item) =>
    //     item.id === action.payload.id
    //       ? { ...item, editable: !item.editable }
    //       : item
    //   );
    // case EDIT_TODO:
    //   return state.map((item) =>
    //     item.id === action.payload.id
    //       ? { ...item, content: action.payload.content }
    //       : item
    //   );
    // case COMPLETE_TODO:
    //   return state.map((item) =>
    //     item.id === action.payload.id
    //       ? { ...item, completed: !item.completed }
    //       : item
    //   );
    default:
      return state;
  }
}
