import React, { useState } from 'react';
import styled from '@emotion/styled';
import Button from '../ui/Button';

const AddNewTodo = () => {
  const [newTodoValue, setNewTodoValue] = useState('');
  const [isNewTodoActive, setIsTodoActive] = useState(false);

  return (
    <AddNewTodoStyled>
      {isNewTodoActive ? (
        <div className='addNewTodoActive'>
          <input type='text' />
          <input type='text' />
        </div>
      ) : (
        <input
          className='addNewInput'
          type='text'
          placeholder='Add new todo ...'
          value={newTodoValue}
          onChange={(e) => setNewTodoValue(e.target.value)}
          onMouseDown={() => setIsTodoActive(true)}
        />
      )}

      <Button>Add Todo</Button>
    </AddNewTodoStyled>
  );
};

const AddNewTodoStyled = styled.div`
  ${({ theme }) => theme.flex.between};

  input {
    ${({ theme }) => theme.input};
  }

  .addNewTodoActive {
    border: 1px solid ${({ theme }) => theme.color.grey};
    border-radius: 5px;
    padding: 20px;
    margin-right: 20px;
    flex-grow: 1;
    flex-shrink: 1;

    input {
      &:not(::last-of-type) {
        margin-bottom: 10px;
      }
    }
  }
`;

export default AddNewTodo;
