const postData = (url = ``, data = {}) => {
  return fetch(url, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(data),
  }).then((response) => response.json());
};

export const getCurrentWeather = () => {
  return fetch(
    'https://api.openweathermap.org/data/2.5/onecall?lat=52.24572126288004&lon=20.964353351569923&appid=537293801af61b846f662e88b4302899'
  )
    .then((res) => res.json())
    .then((data) => console.log(data))
    .catch((err) => console.log(err));
};





