import React from 'react';
import styled from '@emotion/styled';

import './todo-app-input.css';
import { useDispatch } from 'react-redux';
import { addTodo } from '../../store/actions/todo-app.actions';
import { FiPlus } from 'react-icons/fi';
import TooltipComponent from '../tooltip/tooltip.component';

const Input = () => {
  const [value, setValue] = useState('');
  const dispatch = useDispatch();

  const addToList = (e) => {
    dispatch(addTodo(value));
    e.target.value = '';
  };
  return (
    <InputStyled>
      <div className={'pane'}>
        <span>Todo:</span>
        <textarea
          cols='1'
          rows='1'
          className='pane__textarea'
          placeholder='Make a note...'
          onKeyUp={(e) => {
            setValue(e.target.value);
            e.keyCode === 13 && addToList(e);
          }}
        />
        <TooltipComponent title={'add to list'} position={'top'}>
          <button
            type='button'
            className='ui-button'
            onClick={() => dispatch(addTodo(value))}
          >
            <FiPlus />
          </button>
        </TooltipComponent>
      </div>
    </InputStyled>
  );
};

const InputStyled = styled.input`
  .todo-inp {
    width: 100%;
    border: none;
    font-size: 16px;
    padding: 4px 0;
  }

  .pane {
    padding: 10px 12px;
    box-shadow: 3px 10px 10px 3px rgba(204, 204, 204, 1);
    display: grid;
    grid-template-columns: auto 1fr auto;
    grid-column-gap: 5px;
    border-radius: 4px;
    background: var(--paper);
  }

  .pane__textarea {
    align-self: stretch;
    background: inherit;
    font-family: 'Roboto';
    font-size: 14px;
    font-weight: 400;
    font-style: italic;
    border-color: transparent;
    resize: vertical;
    height: 17px;
  }

  .ui-button {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 6px;
    box-shadow: 0 -1px 1px rgba(0, 0, 0, 0.3) inset;
    border-radius: 4px;
    border: none;
    cursor: pointer;
  }

  .ui-button:active {
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.3) inset;
  }
`;
export default Input;
