import React from 'react';
import styled from '@emotion/styled';

const TodoItem = ({ todo }) => {
  return (
    <TodoItemStyled>
      <div className='content'>
        <p className='title'>TITLE: {todo.title}</p>
        <p>DESCRIPTION: {todo.description}</p>
        <hr />
      </div>
    </TodoItemStyled>
  );
};

const TodoItemStyled = styled.li`
  font-size: 20px;
  line-height: 20px;
  margin-bottom: 10px;

  .title {
   font-weight: 600;
   margin-bottom: 10px;
  }

  .content {
    width: 70%;
  }
`;

export default TodoItem;
