import React, { useState } from 'react';
import styled from '@emotion/styled';
import AddNewTodo from './AddNewTodo';

const TodoHeader = () => {
  const [searchValue, setSearchValue] = useState('');
  const [newTodoValue, setNewTodoValue] = useState('');
  const [isNewTodoActive, setIsTodoActive] = useState(false);

  return (
    <TodoHeaderStyled>
      <div className='hero'>
        <span>TODO APE</span>
        <InputStyled
          type='text'
          placeholder='Search ...'
          value={searchValue}
          onChange={(e) => setSearchValue(e.target.value)}
        />
        <ButtonStyled>Search</ButtonStyled>

        <AddNewTodo />
      </div>
    </TodoHeaderStyled>
  );
};

const TodoHeaderStyled = styled.div`
  font-size: 20px;
  line-height: 20px;
  margin-bottom: 50px;

  .hero {
    display: flex;
    align-items: center;
    font-weight: 700;
    margin-bottom: 20px;

    span {
      white-space: nowrap;
      margin-right: 20px;
    }
  }

  .add-new-todo {
    display: flex;
    align-items: center;
  }
`;

const InputStyled = styled.input`
  border-radius: 5px;
  height: 50px;
  padding: 0 20px;
  margin-right: 20px;
  border: 1px solid grey;
  box-sizing: border-box;
`;

const ButtonStyled = styled.button`
  height: 50px;
  background: transparent;
  padding: 0 20px;
  border-radius: 5px;
  border: 1px solid grey;
  white-space: nowrap;
`;

export default TodoHeader;
