import React, { useState } from 'react';
import styled from '@emotion/styled';

const TodoHeader = () => {
  const [searchValue, setSearchValue] = useState('');

  return (
    <TodoHeaderStyled>
      <div className='hero'>
        <span>TODO APE</span>
        <input
          type='text'
          placeholder='Search ...'
          value={searchValue}
          onChange={(e) => setSearchValue(e.target.value)}
        />
        <button>Search</button>
      </div>

      <div className="add-new-"></div>

      <input types='text' />
    </TodoHeaderStyled>
  );
};

const TodoHeaderStyled = styled.div`
  padding: 20px 20px 0;

  .hero {
    display: flex;
    align-items: center;
    font-size: 20px;
    line-height: 20px;
    font-weight: 700;
    margin-bottom: 50px;

    span {
      white-space: nowrap;
      margin-right: 20px;
    }

    input {
      border-radius: 5px;
      height: 50px;
      padding: 0 20px;
      margin-right: 20px;
      border: 1px solid grey;
      box-sizing: border-box;
    }

    button {
      height: 50px;
      background: transparent;
      padding: 0 20px;
      border-radius: 5px;
      border: 1px solid grey;
    }
  }
`;

export default TodoHeader;
